import { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Customized Butterfly Icon
const customIcon = new L.Icon({
    //iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
    iconUrl: 'data:image/png;base64,',
    shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
    iconSize: [35, 35],
    iconAnchor: [12, 41],
});

const AmericanMapLeaflet = () => {
    const [butterflies, setButterflies] = useState([]);

    useEffect(() => {
        fetch('/butterfliesmarkersdb.json')
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                setButterflies(data.butterflies);
            })
            .catch((err) => console.error('Error al cargar mariposas:', err));
    }, []);

    return (
        <><h2 className="project-title">
            Mapa interactivo de Mariposas de Amrica
        </h2><MapContainer
            center={[-10, -60]} // Centro general de Amrica del Sur
            zoom={4}
            scrollWheelZoom={true}
            style={{ height: '100vh', width: '90%', maxWidth: '1200px', margin: '50px auto', }}




        >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; OpenStreetMap contributors' />

                {butterflies.map((butterfly) => (
                    <Marker key={butterfly.id} position={butterfly.position} icon={customIcon}>
                        <Popup maxWidth={250}>
                            <strong>{butterfly.name}</strong>
                            <br />
                            <img
                                src={butterfly.img}
                                alt={butterfly.name}
                                style={{ width: '100%', maxHeight: '120px', objectFit: 'cover', margin: '8px 0' }} />
                            <div><strong>Origen:</strong> {butterfly.origin}</div>
                            <div><strong>Hbitat:</strong> {butterfly.habitat}</div>
                            <div><strong>Ubicacin:</strong> {butterfly.location}</div>
                        </Popup>
                    </Marker>
                ))}
            </MapContainer></>
    );
};

export default AmericanMapLeaflet;
